import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import './App.css';
import Home from './components/pages/Home';
import SignUp from './components/pages/Sign Up/SignUp';
import SignIn from './components/pages/Sign In/SignIn';
import AboutUs from './components/pages/About Us/AboutUs';
import ContactUs from './components/pages/Contact Us/ContactUs';
import Notes from './components/pages/Notes/Notes';
import ExamPal from './components/pages/Exam Pal/ExamPal';
import AdvisorChatbot from './components/pages/Advisor Chatbot/AdvisorChatbot';
import SavedConversation from './components/pages/Archived Chats/SavedConversation'; // Import the SavedConversation component
import CourseSelection from './components/pages/courses/courses'; // Correct path to courses.js



function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const location = useLocation();
  const hideNavbar = location.pathname === '/login' || location.pathname === '/sign-up';

  return (
    <>
      {!hideNavbar && <Navbar />}
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/sign-up' element={<SignUp />} />
        <Route path='/login' element={<SignIn />} />
        <Route path='/about-us' element={<AboutUs />} />
        <Route path='/contact-us' element={<ContactUs />} />
        <Route path='/notes' element={<Notes />} />
        <Route path='/ExamPal' element={<ExamPal />} />
        <Route path='/advisorchatbot' element={<AdvisorChatbot />} />
        <Route path='/saved-conversations' element={<SavedConversation />} />
        <Route path='/courses' element={<CourseSelection />} /> {/* Add the CourseSelection route */}
      </Routes>
    </>
  );
}


export default App;
