import React, { useState, useEffect } from 'react';
import './courses.css';
import { useNavigate } from 'react-router-dom';

function CourseSelection() {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setIsLoading(true);
    setError(null);
    const fetchCourses = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/courses', {
          credentials: 'include'  // Include cookies with the request
        });
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        if (!Array.isArray(data)) {
          throw new Error('Data format is incorrect, expected an array.');
        }
        setCourses(data);
      } catch (e) {
        setError(`Failed to fetch courses: ${e.message}`);
      } finally {
        setIsLoading(false);
      }
    };    

    fetchCourses();
  }, []);

  const handleCourseToggle = (courseId) => {
    setSelectedCourses(prev => {
      if (prev.includes(courseId)) {
        return prev.filter(id => id !== courseId);
      } else {
        return [...prev, courseId];
      }
    });
  };

  const handleSubmit = async () => {
    try {
      const student_id = 1;  // This should be securely retrieved, possibly from a user session or context
      const response = await fetch('http://localhost:5000/api/select-courses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ student_id, selected_courses: selectedCourses }),
        credentials: 'include'  // Include cookies with the request
      });
      const result = await response.json();
      if (!response.ok) {
        throw new Error(result.error || 'Error submitting courses');
      }
      console.log('Courses submitted:', result);
      navigate('/next-route'); // Update this path to where you want users redirected after submission
    } catch (e) {
      setError(`Error during submission: ${e.message}`);
    }
  };

  if (isLoading) return <div>Loading courses...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className='hero-container'>
      <video src='/videos/sign-in.mp4' autoPlay loop muted />
      <h1>Select Your Courses</h1>
      <div className='course-selection-container'>
        <div className='courses-list'>
          {courses.map(course => (
            <div key={course.id} className='course-item'>
              <input
                type="checkbox"
                id={`course_${course.id}`}
                checked={selectedCourses.includes(course.id)}
                onChange={() => handleCourseToggle(course.id)}
              />
              <label htmlFor={`course_${course.id}`}>{course.name}</label>
            </div>
          ))}
        </div>
        <button className='submit-button' onClick={handleSubmit}>Submit Courses</button>
      </div>
    </div>
  );
}

export default CourseSelection;
