import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignUp.css';

function SignUp() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        username: '',
        password: '',
        major: '',
        courses: []
    });
    const [courses, setCourses] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        // Fetch the courses on component mount
        const fetchCourses = async () => {
            const response = await fetch('/courses');
            const data = await response.json();
            setCourses(data.map(course => ({ ...course, checked: false })));
        };
        fetchCourses();
    }, []);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData({ ...formData, [id]: value });
    };

    const handleCourseChange = (courseId) => {
        setCourses(courses.map(course => 
            course.id === courseId ? { ...course, checked: !course.checked } : course
        ));
        updateSelectedCourses(courseId);
    };

    const updateSelectedCourses = (courseId) => {
        const updatedCourses = formData.courses.includes(courseId)
            ? formData.courses.filter(id => id !== courseId)
            : [...formData.courses, courseId];
        setFormData({ ...formData, courses: updatedCourses });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/signup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                navigate('/login');
            } else {
                const errorData = await response.json();
                setErrorMessage(errorData.message);
            }
        } catch (error) {
            console.error('Error during signup:', error);
            setErrorMessage('Signup failed due to network error');
        }
    };

    const filteredCourses = courses.filter(course => course.name.toLowerCase().includes(searchTerm.toLowerCase()));

    return (
        <div className='hero-container'>
            <video src='/videos/sign-in.mp4' autoPlay loop muted />
            <h1>Uniguide</h1>
            <p>The intelligent academic advisor</p>
            <form className='sign-up-form' onSubmit={handleSubmit}>
                {/* Existing inputs */}
                <input type='text' id='searchTerm' placeholder='Search courses...' value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
                <div className='courses-container'>
                    {filteredCourses.map(course => (
                        <div key={course.id} className='course-item'>
                            <input
                                type='checkbox'
                                id={`course_${course.id}`}
                                checked={course.checked}
                                onChange={() => handleCourseChange(course.id)}
                            />
                            <label htmlFor={`course_${course.id}`}>{course.name}</label>
                        </div>
                    ))}
                </div>
                <button type='submit' className='btn btn--primary btn--large'>Sign Up</button>
            </form>
            {errorMessage && <p className='error-message'>{errorMessage}</p>}
            <p className='sign-up-link'>
                Already a user? <a href='/login'>Log in here</a>
            </p>
        </div>
    );
}

export default SignUp;
