import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignIn.css';

function SignIn() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({ username: '', password: '' });
    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData({ ...formData, [id]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:5000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const data = await response.json();
                if (data.status === 'success') {
                    navigate('/home');  // Navigate to the home page if login is successful
                } else {
                    setErrorMessage(data.message);  // Set error message from server response
                }
            } else {
                console.error('HTTP error:', response.status);  // Log HTTP errors to console
                setErrorMessage('Login failed due to server error');  // Generic error message for the user
            }
        } catch (error) {
            console.error('Error during login:', error);  // Log network or parsing errors to console
            setErrorMessage('Login failed due to network error');  // Network error message for the user
        }
    };

    return (
        <div className='hero-container'>
            <video src='/videos/sign-in.mp4' autoPlay loop muted />
            <h1>Uniguide</h1>
            <p>The intelligent academic advisor</p>
            <form className='sign-in-form' onSubmit={handleSubmit}>
                <input 
                    type='text' 
                    id='username' 
                    placeholder='Username' 
                    value={formData.username} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type='password' 
                    id='password' 
                    placeholder='Password' 
                    value={formData.password} 
                    onChange={handleChange} 
                    required 
                />
                <button type='submit' className='btn btn--primary btn--large'>
                    Sign In
                </button>
            </form>
            {errorMessage && <p className='error-message'>{errorMessage}</p>}
            <p className='sign-up-link'>
                Not a user? <a href='/sign-up'>Sign up here</a>
            </p>
        </div>
    );
}

export default SignIn;
